import React from 'react';
import { Row, Col, Image, Progress, Button } from 'antd';
import { DeleteOutlined } from '@ant-design/icons';
import { IPokemon, TPokedex, Languages } from '../types/pokemon';

interface IPokedexProps {
  pokedex: TPokedex;
  language: Languages;
  onDeletePokedexPokemon: (pokemon: IPokemon) => Promise<void>;
}

const Pokedex: React.FunctionComponent<IPokedexProps> = (props) => {
  const { pokedex, language, onDeletePokedexPokemon } = props;

  const deletePokemon = (pokemon: IPokemon) => {
    return async (): Promise<void> => {
      await onDeletePokedexPokemon(pokemon);
    }
  };

  return (
    <div className="pokedex">
      { pokedex.length ? (
        pokedex.map((pokemon: IPokemon) => {
          return (
            <Row gutter={12} className="pokedex-pokemon">
              <Col span={8}>
                <div className="pokedex-info">
                  <Image
                    width={68}
                    className="pokemon-image"
                    height={68}
                    src={`${process.env.PUBLIC_URL}/icons/${pokemon.name.english.toLowerCase()}.png`}
                    fallback="data:image/png;base64,"
                  />
                  <h3>{pokemon.name[language]}</h3>
                </div>
              </Col>
              <Col span={12}>
                { pokemon.base && 
                  Object.entries(pokemon.base).map(base => {
                    const [ property, value ] = base;
                    return (
                      <Row gutter={8} key={property}>
                        <Col span={8}>{property}</Col>
                        <Col span={12}><Progress percent={value} /></Col>
                      </Row>
                    );
                  })
                }
              </Col>
              <Col span={4}>
                  <div className="pokedex-remove">
                    <Button type="primary" icon={<DeleteOutlined />} onClick={deletePokemon(pokemon)}>Delete</Button>
                  </div>
              </Col>
            </Row>
          );
        })
      ) : (
        <h1>You have no pokemons in your pokedex</h1>
      )
      }
    </div>
  );
}

export default Pokedex;
